// Funkcija, lai iestatÄ«tu valodu
function setLanguage(lang) {
    localStorage.setItem('language', lang); // SaglabÄjam valodu
    const elements = document.querySelectorAll('[data-translate]');
    elements.forEach(element => {
        const key = element.getAttribute('data-translate');
        element.textContent = translations[lang][key] || element.textContent;
    });

    // Atjaunojam HTML lang atribÅ«tu
    document.documentElement.lang = lang;

    // Atjaunojam href atribÅ«tus saitÄ“m, kas satur tulkojumu atslÄ“gas
    const links = document.querySelectorAll('a[data-translate-href]');
    links.forEach(link => {
        const key = link.getAttribute('data-translate-href');
        link.textContent = translations[lang][key] || link.textContent;
    });
}

// Funkcija, lai inicializÄ“tu valodu lapas ielÄdes brÄ«dÄ«
document.addEventListener('DOMContentLoaded', () => {
    const savedLang = localStorage.getItem('language') || 'lv'; // NoklusÄ“juma valoda - latvieÅ¡u
    setLanguage(savedLang);

    // TÄ“mas inicializÄcija
    const savedTheme = localStorage.getItem('theme') || 'light';
    document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    updateThemeIcon(savedTheme);
});

// Funkcija, lai pÄrslÄ“gtu tÄ“mu
function toggleTheme() {
    const isDark = document.body.classList.toggle('dark-theme');
    const theme = isDark ? 'dark' : 'light';
    localStorage.setItem('theme', theme);
    updateThemeIcon(theme);
}

// Funkcija, lai atjauninÄtu tÄ“mas ikonu
